html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        line-height: 90%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding: 10mm;
            min-height: 297mm;
            footer {page-break-after: always;}
        }
    }
</style>

<small>
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
<table class="table table-condensed table-bordered">
  <tr>
    {%- if doc.taxes_and_charges == 'OGL'-%}
        <td colspan="3">
            <p style="text-align:center; font-size:14px"><b>{{ _("PROFORMA INVOICE") }}</b></p>
        </td>
    {% else %}
        <td colspan="2">
        {%- if frappe.db.get_value("Sales Taxes and Charges Template", 
            doc.taxes_and_charges, "is_export") == 1 -%}
            <p style="text-align:left; font-size:14px"><b>{{ _("EXPORT INVOICE") }}</b></p>
        {%- elif doc.billing_address_gstin|length == 15 -%}
            <p style="text-align:left; font-size:14px"><b>{{ _("TAX INVOICE") }}</b></p>
        {% else %}
            <p style="text-align:left; font-size:14px"><b>{{ _("TAX INVOICE (UNREGISTERED- NO GST CREDIT)") }}</b></p>
        {%- endif -%}
        </td>
        <td style="width: 34%;">
            <p style="text-align:right; font-size:14px"><b>{{ _("Invoice #: ") }} {{ doc.name }}</b></p>
        </td>
    {%- endif -%}
  </tr>
</table>

<table class = "table table-condensed table-bordered">
    <tr>
        <th syle="width: 33%;">
            <b>{{ _("Details of Receiver (Bill To)") }}</b>
        </th>
        <th style="width: 33%;">
            <b>{{ _("Details of Consignee (Ship To)") }}</b>
        </th>
        <th style="width: 34%;">
            <b>{{ _("Other Details") }}</b>
        </th>
    </tr>
  <tr>
    <td style="width: 33%;">
        <b>{{ _("Name") }}:</b> {{ frappe.db.get_value("Address", doc.customer_address, "address_title") }}<br>
        <b>{{ _("Address") }}:</b> {{ doc.address_display }} <br>
        {%- if doc.billing_address_gstin|length == 15 -%}
            <b>{{ _("GSTIN") }}:</b> {{ doc.billing_address_gstin }}<br>
        {%- endif -%}
        {%- if frappe.db.get_value("State", (frappe.db.get_value("Address", doc.customer_address, 
            "state_rigpl")), "name"): -%}

            <b>{{ _("State & Code") }}:</b> {{ frappe.db.get_value("Address", 
                doc.customer_address, "state_rigpl") }} {{ _("-")}} {{ _(frappe.db.get_value("State", 
                (frappe.db.get_value("Address", 
                doc.customer_address, "state_rigpl")), "state_code_numeric"))}}

        {%- endif -%}
    </td>
    <td style="width: 33%;">
        <b>{{ _("Name") }}:</b> {{ frappe.db.get_value("Address", doc.shipping_address_name, "address_title") }}<br>
        <b>{{ _("Address") }}:</b> {{ doc.shipping_address }}<br>
        {%- if doc.shipping_address_gstin|length == 15 -%}
            <b>{{ _("GSTIN") }}:</b> {{ doc.shipping_address_gstin }}<br>
        {%- endif -%}
        {%- if frappe.db.get_value("State", (frappe.db.get_value("Address", doc.shipping_address_name, 
            "state_rigpl")), "name"): -%}

            <b>{{ _("State & Code") }}:</b> {{ frappe.db.get_value("Address", 
                doc.shipping_address_name, "state_rigpl") }} {{ _("-")}} {{ _(frappe.db.get_value("State", 
                (frappe.db.get_value("Address", 
                doc.shipping_address_name, "state_rigpl")), "state_code_numeric"))}}

        {%- endif -%}

    </td>
    <td style="width: 34%;">
        <b>{{ _("Date") }}:</b> {{ doc.get_formatted("posting_date") }}<br>
        <b>{{ _("Due Date") }}:</b> {{ doc.get_formatted("due_date") }}<br>
        {%- if doc.removal_date != doc.posting_date -%}
            <b>{{ _("Removal Date") }}:</b> {{ doc.get_formatted("removal_date") }}<br>
            <b>{{ _("Removal Time") }}:</b> {{ doc.get_formatted("removal_time") }}<br>
        {%- endif -%}
        <b>{{ _("Ship Via") }}:</b> {{ doc.transporters }}<br>
        <b>{{ _("Docket #") }}:</b> {{ doc.lr_no }}<br>
        {%- if frappe.db.get_value("Sales Taxes and Charges Template", 
            doc.taxes_and_charges, "state"): -%}

            <b>{{ _("Supply State & Code") }}:</b> {{ (" ") }} {{
                frappe.db.get_value("Sales Taxes and Charges Template", 
                doc.taxes_and_charges, "state") }} {{ ("-") }} {{ (
                frappe.db.get_value("State", (frappe.db.get_value("Sales Taxes and Charges Template", 
                doc.taxes_and_charges, "state")), "state_code_numeric")) }}

        {%- endif -%}
    </td>
  </tr>
  <tr>
    {%- if doc.currency != 'INR' -%}
        <td colspan="2"; style="width: 34%;">
            <b>{{ _("PO Date") }}:</b> {{ doc.get_formatted("po_date") }},
            <b>{{ _("PO #") }}:</b> {{ doc.po_no }}
        </td>
        <td>
             <b>{{ _("Currency Info") }}:</b> {{ _("1 -") }}{{ doc.currency }} 
                {{ _("=") }} {{ _(doc.conversion_rate) }} {{ _(" INR") }}
        </td>
    {%- else -%}
        <td>
            <b>{{ _("PO Date") }}:</b> {{ doc.get_formatted("po_date") }},
            <b>{{ _("PO #") }}:</b> {{ doc.po_no }}
        </td>
    {%- endif -%}
  </tr>
  {%- if doc.letter_head == 'RIGB' -%}
        {%- if frappe.db.get_value("Sales Taxes and Charges Template", 
            doc.taxes_and_charges, "is_export") == 1 -%}
          <tr>
            <td colspan = "3">
                <b>{{ _("Beneficiary Name") }}:</b> {{ frappe.db.get_value("Letter Head", doc.letter_head, "organisation_name") }},
                <b>{{ _("Bank Name") }}:</b> {{ "Kotak Mahindra Bank" }} <br>
                <b>{{ _("Account No") }}:</b> {{ "4011995232" }}, 
                <b>{{ _("Swift Code") }}:</b> {{ "KKBKINBBCPC" }},
                <b>{{ _("Branch") }}:</b> {{ "Punjabi Bagh, New Delhi, India" }}
            </td>
          </tr>
        {%- else -%}
          <tr>
            <td colspan = "3">
                <b>{{ _("Beneficiary Name") }}:</b> {{ frappe.db.get_value("Letter Head", doc.letter_head, "organisation_name") }},
                <b>{{ _("Bank Name") }}:</b> {{ "Citibank NA" }} <br>
                <b>{{ _("Account No") }}:</b> {{ "0418780223" }}, 
                <b>{{ _("IFSC Code") }}:</b> {{ "CITI0000031" }},
                <b>{{ _("Branch") }}:</b> {{ "Punjabi Bagh, New Delhi" }}
            </td>
          </tr>
        {%- endif -%}
    {%- endif -%}
</table>

<table class="table table-condensed table-hover table-bordered">
        <tr>
            <th>Sr</th>
            <th>Description</th>
            <th>HSN/SAC</th>
            <th class="text-right">Qty</th>
            <th class="text-right">Rate</th>
            <th class="text-right">Amount</th>
        </tr>
        {%- for row in doc.items -%}
        <tr>
            <td style="width: 3%;">{{ row.idx }}</td>
            <td style="width: 50%;">{% if frappe.db.get_value("Item", row.item_code, "variant_of") %}
                {{ ("Item Code") }}: {{ row.item_code }}; {{ ("Description: ") }}
                {%- endif -%}
                {{ row.description }}
            </td>
            <td style="width: 7%;">{{ row.gst_hsn_code }}</td>
            <td style="width: 10%; text-align: right;">{{ row.qty }} {{ row.uom or row.stock_uom }}</td>
            <td style="width: 15%; text-align: right;">{{
                row.get_formatted("net_rate", doc) }}</td>
            <td style="width: 15%; text-align: right;">{{
                row.get_formatted("net_amount", doc) }}</td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>
<table class="table table-condensed table-bordered">
  <tr>
    <td width="50%">
        <p>{% if doc.terms %} <b>{{ _("Auxiliary Information") }}: </b>{{ doc.terms or "" }}
        {%- endif -%}
    </p>
    <p style="font-size:12px">{% if doc.in_words %}
        <b>{{ _("Total Amount (In Words)") }}: </b><br>
            {{ doc.in_words }}
        {%- endif -%}
    </p>
    <table class="table table-condensed">
      <tr>
        <td style="width: 70%"; height="100px"; valign="top">
            <b>{{ _("For ") }} {{ (frappe.db.get_value("Letter Head", doc.letter_head, "organisation_name")) }}</b>
        </td>

        <td style ="text-align:right"; height="100px"; valign="top">
            <b>{{ _("Received By:") }}</b>
        </td>
      </tr>
    </table>

    </td>
<td>
        <table class="table table-condensed">
            <tr>
                <td class="text-right" style="width: 30%">
                    {{ _("Net Total") }}
                </td>
                <td class="text-right">
                    {{ doc.get_formatted("net_total") }}
                </td>
            </tr>
            {%- for row in doc.taxes -%}
            {%- if row.tax_amount != 0 -%}
            <tr height:100%>
                <td class="text-right" style="width: 70%">
                    {{ row.description }}
                </td>
                <td class="text-right">
                    {{ row.get_formatted("tax_amount", doc) }}
                </td>
            </tr>
            {%- endif -%}
            {%- endfor -%}
            {%- if doc.discount_amount -%}
            <tr>
                <td class="text-right" style="width: 70%">
                    {{ _("Discount") }}
                </td>
                <td class="text-right">
                    {{ doc.get_formatted("discount_amount") }}
                </td>
            </tr>
            {%- endif -%}
            <tr>
                <td class="text-right" style="width: 70%">
                    <big><b>{{ _("Grand Total") }}</b></big>
                </td>
                <td class="text-right">
                    <big><b>{{ doc.get_formatted("grand_total") }}</b></big>
                </td>
            </tr>
        </td>

        </table>

    </td>
  </tr>
</table>
</small>
